package com.java.basicprogram;/** * uses a for loop to display numbers in descending order in the step of 5 * starting from 100. */public class BackwardCounting {    public static void main(String[] args) {        BackwardCounting object=new BackwardCounting();        object.backwordCounter();    }   /**            * This method counts backward		 *                 */   public void backwordCounter(){       for (int i=100;i>=0;i=i-5) {           System.out.print(i+" ");       }   }}