package com.java.basicprogram;public class CheckPalindromeNumber {    public static void main(String[] args) {        checkPalindromeNumber();    }    public static void checkPalindromeNumber(){        int reminder, sum=0,temp;        int isPelindromeNumber=787;        temp=787;        while (isPelindromeNumber>0){            reminder=isPelindromeNumber%10;            sum=sum*10+reminder;            isPelindromeNumber=isPelindromeNumber/10;        }        if (temp==sum) {            System.out.println("Given number is palindrome number.");        }else {            System.out.println("Given number is not palindrome number.");        }    }}