package com.java.basicprogram;import java.util.Scanner;public class CheckPalindromeNumber2 {    public static void checkPalindromeNumber(){        String original, reverse="";        Scanner scanner=new Scanner(System.in);        System.out.println("Enter a string/number to check if it is palindrome.");        original=scanner.nextLine();        int length= original.length();        for (int i=length-1;i>=0;i--) {            reverse =reverse+original.charAt(i);        }        if (reverse.equals(original)) {            System.out.println("Entered string/number is a palindrome.");        }else {            System.out.println("Entered string/number is not a palindrome.");        }    }    public static void main(String[] args) {        checkPalindromeNumber();    }}