package com.java.basicprogram;public class FindIfStringHasUniqueCharacter {    public static void main(String[] args) {        boolean checkUnique=findStringHasUniqueCharacter("narender");        System.out.println("narender has all unique character : "+ checkUnique);        checkUnique=findStringHasUniqueCharacter("index");        System.out.println("index has all unique character : "+ checkUnique);        checkUnique=findStringHasUniqueCharacter("apple");        System.out.println("apple has all unique character : "+ checkUnique);        checkUnique=findStringHasUniqueCharacter("world");        System.out.println("world has all unique character : "+ checkUnique);    }    public static boolean findStringHasUniqueCharacter(String string){        for (int i=0;i<string.length();i++) {            char ch= string.charAt(i);            if (string.indexOf(ch)!= string.lastIndexOf(ch)) {                return false;            }        }        return true;    }}