package com.java.basicprogram;// Swap value of one number to anotherpublic class SwapValue {    public static void main(String[] args) {        int number1= 10;        int number2=20;        int number3;        System.out.println("Value of number1 before swapping = "+ number1);        System.out.println("Value of number2 before swapping = "+ number2);        number3=number1;        number1=number2;        number2=number3;        System.out.println("Value of number1 after swapping = "+ number1);        System.out.println("Value of number2 after swapping = "+ number2);    }}